import * as THREE from 'three';

const loader = new THREE.OBJLoader();
const mtlLoader = new THREE.MTLLoader();

// First, load the .mtl file
mtlLoader.load('/models/airplane/11803_Airplane_v1_l1.mtl', function(materials) {
    materials.preload();
    
    // Once the materials are loaded, load the .obj file
    loader.setMaterials(materials);
    loader.load('/models/airplane/11803_Airplane_v1_l1.obj', function(object) {
        scene.add(object);  // Add the object to the 3D scene
    });
});
